namespace GameShop.Data.Seeding
{
    using System;
    using System.Collections.Generic;
    using System.Linq;
    using System.Text;
    using System.Threading.Tasks;

    using GameShop.Data.Models;

    internal class GameSeeder : ISeeder
    {
        public async Task SeedAsync(ApplicationDbContext dbContext, IServiceProvider serviceProvider)
        {
            if (dbContext.Games.Any())
            {
                return;
            }

            var games = new List<Game>
            {
                new Game
                {
                    Title = "Quake2",
                    Price = 35,
                    ImageURL = "https://steamcdn-a.akamaihd.net/steam/apps/2320/0000002451.600x338.jpg?t=1572894487",
                    RealaseDate = DateTime.UtcNow,
                    Description = "Quake II is a first-person shooter video game released in December 1997. It was developed by id Software and published by Activision. It is not a direct sequel to Quake; id decided to revert to an existing trademark when the game's fast-paced, tactile feel felt closer to a Quake game than a new franchise.[6] The game's storyline is continued in its expansions and Quake 4.",
                },
                new Game
                {
                    Title ="Counter-Strike",
                    Price = 11,
                    ImageURL="data:image/jpeg;base64,",
                    RealaseDate=DateTime.UtcNow,
                    Description="Counter-Strike (CS) is a series of multiplayer first-person shooter video games in which teams of terrorists battle to perpetrate an act of terror (bombing, hostage-taking, assassination) while counter-terrorists try to prevent it (bomb defusal, hostage rescue).",
                },
                new Game
              {
                    Title ="Half-Life",
                    Price = 99,
                    ImageURL="https://upload.wikimedia.org/wikipedia/en/thumb/f/fa/Half-Life_Cover_Art.jpg/220px-Half-Life_Cover_Art.jpg",
                    Description="Half-Life is a first-person shooter video game developed by Valve and published by Sierra Studios for Microsoft Windows in 1998. It was Valve's debut product and the first game in the Half-Life series.",
              },
            };

            await dbContext.Games.AddRangeAsync(games);
        }
    }
}
